// attio.js
const ATTIO_API_BASE = "https://api.attio.com/v2";
const BEARER_TOKEN = "Bearer e01cca9d5d70d62535755e3f1609118082790728f8c98dbd0b3f9cce1aae3f53";

// In-memory cache for object IDs (e.g., people, companies)
const objectCache = {};

// MAIN DISPATCHER
async function sendToAttio(structuredUpdates) {
  for (const item of structuredUpdates) {
    try {
      ensureFields(item);
      const intent = detectIntent(item);

      if (item.type === "person") {
        if (intent === "delete") {
          await deletePersonByName(item.name);
        } else {
          await upsertPerson(item);
        }
      }

      if (item.type === "task") {
        if (intent === "delete") {
          await deleteTaskByDescription(item.description);
        } else {
          await upsertTask(item);
        }
      }

    } catch (err) {
      console.error("‚ùå Failed to process item:", item, err);
    }
  }
}

// ========== OBJECT ID RESOLUTION ==========

async function getObjectIdBySlug(slug) {
  if (objectCache[slug]) return objectCache[slug];

  const res = await fetch(`${ATTIO_API_BASE}/objects`, {
    headers: { Authorization: BEARER_TOKEN }
  });

  const json = await res.json();
  const match = json.data?.find(obj => obj.api_slug === slug);

  if (match) {
    objectCache[slug] = match.id;
    return match.id;
  } else {
    throw new Error(`‚ùå Could not find object with slug "${slug}"`);
  }
}

// ========== UTILS ==========

function detectIntent(item) {
  const txt = (item.notes || item.description || "").toLowerCase();
  if (txt.includes("remove") || txt.includes("cancel")) return "delete";
  if (txt.includes("changed") || txt.includes("update")) return "update";
  return "upsert";
}

function ensureFields(item) {
  if (item.type === "person") {
    item.name = item.name || "Unknown";
    item.notes = item.notes || "";
  }

  if (item.type === "task") {
    item.description = item.description || "Untitled task";
    item.due_date = parseDateTime(item.due_date, item.due_time);
    item.linked_records = item.linked_records || [];
    item.assignees = item.assignees || [];
  }
}

function parseDateTime(dueDate, dueTime) {
  try {
    let date = new Date();
    if (dueDate?.toLowerCase().includes("tomorrow")) {
      date.setDate(date.getDate() + 1);
    } else {
      const parsed = new Date(dueDate);
      if (!isNaN(parsed)) date = parsed;
    }

    if (dueTime) {
      const [timePart, meridian] = dueTime.split(" ");
      let [hour, minute] = timePart.split(":").map(Number);
      if (meridian?.toLowerCase().includes("p") && hour < 12) hour += 12;
      if (meridian?.toLowerCase().includes("a") && hour === 12) hour = 0;
      date.setHours(hour || 0, minute || 0, 0, 0);
    }

    return date.toISOString();
  } catch {
    return new Date().toISOString();
  }
}

// ========== PERSONS ==========

async function upsertPerson(data) {
  const peopleId = await getObjectIdBySlug("people");
  const existingId = await findPersonRecordIdByName(data.name);

  const [firstName, ...rest] = data.name.split(" ");
  const lastName = rest.join(" ");

  const payload = {
    data: {
      properties: {
        first_name: firstName,
        last_name: lastName,
        notes: data.notes
      }
    }
  };

  const url = existingId
    ? `${ATTIO_API_BASE}/objects/${peopleId}/records/${existingId}`
    : `${ATTIO_API_BASE}/objects/${peopleId}/records`;

  const method = existingId ? "PATCH" : "POST";

  const res = await fetch(url, {
    method,
    headers: {
      Authorization: BEARER_TOKEN,
      "Content-Type": "application/json"
    },
    body: JSON.stringify(payload)
  });

  const json = await res.json();

  if (res.ok) {
    console.log(`‚úÖ Person ${existingId ? "updated" : "created"}:`, json);
    return json.data?.id;
  } else {
    console.error("‚ùå Person sync error:", json);
    return null;
  }
}

async function deletePersonByName(name) {
  const peopleId = await getObjectIdBySlug("people");
  const id = await findPersonRecordIdByName(name);
  if (!id) return console.warn("‚ö†Ô∏è Person not found:", name);

  const res = await fetch(`${ATTIO_API_BASE}/objects/${peopleId}/records/${id}`, {
    method: "DELETE",
    headers: { Authorization: BEARER_TOKEN }
  });

  if (res.ok) {
    console.log("üóëÔ∏è Deleted person:", name);
  } else {
    console.error("‚ùå Failed to delete person:", name);
  }
}

async function findPersonRecordIdByName(name) {
  const peopleId = await getObjectIdBySlug("people");

  const res = await fetch(`${ATTIO_API_BASE}/objects/${peopleId}/records`, {
    headers: { Authorization: BEARER_TOKEN }
  });

  const json = await res.json();
  const match = json.data?.find(entry => {
    const full = `${entry.properties?.first_name || ""} ${entry.properties?.last_name || ""}`.trim().toLowerCase();
    return full === name.toLowerCase();
  });

  return match?.id || null;
}

// ========== TASKS ==========

async function upsertTask(data) {
  await deleteTaskByDescription(data.description); // Prevent duplicates

  const personId = data.link_to_person_name
    ? await findPersonRecordIdByName(data.link_to_person_name)
    : null;

  const payload = {
    data: {
      content: data.description,
      format: "plaintext",
      deadline_at: data.due_date,
      is_completed: false,
      linked_records: personId
        ? [{ target_object: "people", target_record_id: personId }]
        : [],
      assignees: data.assignees
    }
  };

  const res = await fetch(`${ATTIO_API_BASE}/tasks`, {
    method: "POST",
    headers: {
      Authorization: BEARER_TOKEN,
      "Content-Type": "application/json"
    },
    body: JSON.stringify(payload)
  });

  const json = await res.json();

  if (res.ok) {
    console.log("‚úÖ Task created:", json);
  } else {
    console.error("‚ùå Task creation error:", json);
  }
}

async function deleteTaskByDescription(desc) {
  const res = await fetch(`${ATTIO_API_BASE}/tasks`, {
    headers: { Authorization: BEARER_TOKEN }
  });

  const json = await res.json();

  const match = json.data?.find(
    t => t.content?.trim().toLowerCase() === desc.trim().toLowerCase()
  );

  if (!match) return;

  const del = await fetch(`${ATTIO_API_BASE}/tasks/${match.id}`, {
    method: "DELETE",
    headers: { Authorization: BEARER_TOKEN }
  });

  if (del.ok) {
    console.log("üóëÔ∏è Deleted task:", desc);
  } else {
    console.error("‚ùå Failed to delete task:", desc);
  }
}

export { sendToAttio };
