// attio.js
const ATTIO_API_BASE = "https://api.attio.com/v2";
const BEARER_TOKEN = "Bearer e01cca9d5d70d62535755e3f1609118082790728f8c98dbd0b3f9cce1aae3f53";


const objectCache = {}; // caches object_ids like "people"

const ATTRIBUTE_IDS = {
  name: "3a2d79ac-1c54-4d1d-9bd1-df92ba80052f"
};


async function sendToAttio(updates) {
  for (const item of updates) {
    try {
      ensureFields(item);

      if (item.type === "person") {
        await upsertPerson(item);
      }

      if (item.type === "task") {
        await upsertTask(item);
      }
    } catch (err) {
      console.error("❌ Failed to process item:", item, err);
    }
  }
}

function ensureFields(item) {
  if (item.type === "person") {
    item.name = item.name || "Unknown";
    item.notes = item.notes || "";

    const parts = item.name.split(" ");
    item.first_name = parts[0] || "Unknown";
    item.last_name = parts.slice(1).join(" ") || "";
  }

  if (item.type === "task") {
    item.description = item.description || "Untitled task";
    item.due_date = parseDateTime(item.due_date, item.due_time);
  }
}


function parseDateTime(date, time) {
  try {
    let d = new Date();
    if (date?.toLowerCase().includes("tomorrow")) d.setDate(d.getDate() + 1);
    else if (Date.parse(date)) d = new Date(date);

    if (time) {
      const [t, mer] = time.split(" ");
      let [h, m] = t.split(":" ).map(Number);
      if (mer?.includes("p") && h < 12) h += 12;
      if (mer?.includes("a") && h === 12) h = 0;
      d.setHours(h || 0, m || 0, 0, 0);
    }

    return d.toISOString();
  } catch {
    return new Date().toISOString();
  }
}

async function getObjectIdBySlug(slug) {
  if (objectCache[slug]) return objectCache[slug];

  const res = await fetch(`${ATTIO_API_BASE}/objects/${slug}`, {
    headers: { Authorization: BEARER_TOKEN }
  });

  const json = await res.json();
  const id = json.data?.id?.object_id;

  if (!id) throw new Error(`❌ Cannot resolve object slug: ${slug}`);

  objectCache[slug] = id;
  return id;
}

// ========== PERSONS ==========


async function upsertPerson(data) {
  const peopleId = await getObjectIdBySlug("people");

  const values = {
    [ATTRIBUTE_IDS.name]: {
      first: data.first_name,
      last: data.last_name
    }
  };

  const payload = JSON.stringify({ data: { values } });

  const res = await fetch(`${ATTIO_API_BASE}/objects/${peopleId}/records`, {
    method: "POST",
    headers: {
      Authorization: BEARER_TOKEN,
      "Content-Type": "application/json"
    },
    body: payload
  });

  const json = await res.json();
  if (!res.ok) {
    console.error("❌ Person creation error:", json);
  } else {
    console.log("✅ Person created:", json);
  }

  return json.data?.id?.record_id;
}



async function queryPersonByName(name) {
  const peopleId = await getObjectIdBySlug("people");

  const payload = {
    filter: { name },
    limit: 1,
    offset: 0
  };

  const res = await fetch(`${ATTIO_API_BASE}/objects/${peopleId}/records/query`, {
    method: "POST",
    headers: {
      Authorization: BEARER_TOKEN,
      "Content-Type": "application/json"
    },
    body: JSON.stringify(payload)
  });

  const json = await res.json();
  return json.data?.[0] || null;
}

// ========== TASKS ==========

async function upsertTask(data) {
  const taskContent = `@${data.link_to_person_name || ""} ${data.description}`.trim();

  const payload = {
    data: {
      content: taskContent,
      format: "plaintext",
      deadline_at: data.due_date,
      is_completed: false,
      assignees: []
    }
  };

  const res = await fetch(`${ATTIO_API_BASE}/tasks`, {
    method: "POST",
    headers: {
      Authorization: BEARER_TOKEN,
      "Content-Type": "application/json"
    },
    body: JSON.stringify(payload)
  });

  const json = await res.json();
  if (res.ok) {
    console.log("✅ Task created:", json);
  } else {
    console.error("❌ Task creation failed:", json);
  }
}

export { sendToAttio };
