import React, { useState, useEffect, useRef } from 'react';
import { Mic, MicOff } from 'lucide-react';
import './speech-recorder.css';
import { sendToAttio, initializeAttributeIds } from './attio'; // Updated Attio integration

const SpeechRecorder = () => {
  const [isRecording, setIsRecording] = useState(false);
  const [currentLines, setCurrentLines] = useState(['', '']);
  const [previousLines, setPreviousLines] = useState(['', '']);
  const [isAnimating, setIsAnimating] = useState(false);
  const recognitionRef = useRef(null);
  const currentTextRef = useRef('');
  const fullTranscriptRef = useRef('');
  const triggeredRef = useRef(false);
  const crmCaptureRef = useRef('');

  const triggerPhrase = "initiate CRM";

  // Initialize attribute IDs on component mount
  useEffect(() => {
    initializeAttributeIds();
  }, []);

  const sendToDeepSeek = async (text) => {
    const prompt = `
You are an Attio CRM enrichment assistant. Extract structured updates from sales conversations and return ONLY a valid JSON array.

CRITICAL RULES:
1. Always create a person object when a name is mentioned
2. Always create a task when there's an action item or follow-up mentioned
3. Link tasks to people using "link_to_person_name" field
4. Use ISO 8601 date format for all dates
5. Extract emails, phone numbers, and other contact info
6. Return ONLY the JSON array, no other text

EXAMPLE INPUT: "just had a talk with John Smith and he is really interested. he wants to meet next week to discuss the proposal"
EXAMPLE OUTPUT:
[
  {
    "type": "person",
    "name": "John Smith",
    "first_name": "John",
    "last_name": "Smith",
    "notes": "Really interested in our proposal"
  },
  {
    "type": "task",
    "name": "Meet with John Smith to discuss proposal",
    "description": "Follow up meeting with John Smith to discuss the proposal",
    "due_date": "2025-08-06T15:00:00Z",
    "link_to_person_name": "John Smith"
  }
]

TRANSCRIPT TO PROCESS:
"""
${text}
"""

Return only the JSON array:`;

    try {
      const response = await fetch('https://openrouter.ai/api/v1/chat/completions', {
        method: 'POST',
        headers: {
          'Authorization': 'Bearer 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',
          'Content-Type': 'application/json',
          'HTTP-Referer': 'http://localhost:3000',
          'X-Title': 'Attio CRM Parser'
        },
        body: JSON.stringify({
          model: 'deepseek/deepseek-r1:free',
          messages: [
            {
              role: 'system',
              content: 'You are a CRM data extraction assistant. Return only valid JSON arrays with person and task objects. Always link tasks to people when mentioned together.'
            },
            {
              role: 'user',
              content: prompt
            }
          ],
          temperature: 0.1 // Lower temperature for more consistent parsing
        })
      });

      const data = await response.json();
      const raw = data.choices?.[0]?.message?.content;
      if (!raw) {
        console.warn('âŒ No response from DeepSeek');
        return [];
      }

      console.log('ðŸ¤– Raw DeepSeek response:', raw);

      // Clean the response - remove markdown and extra text
      let cleaned = raw.replace(/```json|```/g, '').trim();

      // Find JSON array in the response
      const jsonMatch = cleaned.match(/\[[\s\S]*\]/);
      if (jsonMatch) {
        cleaned = jsonMatch[0];
      }

      console.log('ðŸ§¹ Cleaned response:', cleaned);

      const parsed = JSON.parse(cleaned);

      if (!Array.isArray(parsed)) {
        console.warn('âŒ Response is not an array');
        return [];
      }

      // Enhance parsed data with defaults and validation
      parsed.forEach(item => {
        if (item.type === 'task') {
          // Ensure task has required fields
          item.name = item.name || item.description || 'Follow-up task';
          item.description = item.description || item.name || 'Follow-up task';
          item.due_date = item.due_date || getDefaultDueDate();
          item.linked_records = item.linked_records || [];
          item.assignees = item.assignees || [];

          // If no due date specified but mentions "next week", set it
          if (!item.due_date && (item.description.toLowerCase().includes('next week') || item.name.toLowerCase().includes('next week'))) {
            const nextWeek = new Date();
            nextWeek.setDate(nextWeek.getDate() + 7);
            item.due_date = nextWeek.toISOString();
          }
        }

        if (item.type === 'person') {
          item.notes = item.notes || '';

          // Extract name parts if not provided
          if (item.name && (!item.first_name || !item.last_name)) {
            const [first, ...rest] = item.name.split(' ');
            item.first_name = item.first_name || first || 'Unknown';
            item.last_name = item.last_name || rest.join(' ') || '';
          }
        }
      });

      console.log('âœ… Parsed and enhanced data:', parsed);
      return parsed;

    } catch (err) {
      console.error('âŒ Failed to parse DeepSeek response:', err);
      return [];
    }
  };

  const getDefaultDueDate = () => {
    const nextWeek = new Date();
    nextWeek.setDate(nextWeek.getDate() + 7);
    nextWeek.setHours(14, 0, 0, 0); // Default to 2 PM next week
    return nextWeek.toISOString();
  };

  const updateLyricDisplay = (text) => {
    const words = text.split(' ');
    const wordsPerLine = 8;
    if (words.length > wordsPerLine * 2) {
      if (!isAnimating) {
        setIsAnimating(true);
        setPreviousLines([currentLines[0], currentLines[1]]);
        const newLine1 = words.slice(-wordsPerLine * 2, -wordsPerLine).join(' ');
        const newLine2 = words.slice(-wordsPerLine).join(' ');
        setTimeout(() => {
          setCurrentLines([newLine1, newLine2]);
          setTimeout(() => {
            setIsAnimating(false);
            setTimeout(() => {
              setPreviousLines(['', '']);
            }, 500);
          }, 100);
        }, 50);
        currentTextRef.current = newLine1 + ' ' + newLine2 + ' ';
      }
    } else {
      const line1 = words.slice(0, wordsPerLine).join(' ');
      const line2 = words.slice(wordsPerLine).join(' ');
      setCurrentLines([line1, line2]);
    }
  };

  const toggleRecording = async () => {
    if (!isRecording) {
      try {
        await navigator.mediaDevices.getUserMedia({ audio: true });
        recognitionRef.current.start();
        setIsRecording(true);
        setCurrentLines(['', '']);
        setPreviousLines(['', '']);
        currentTextRef.current = '';
        fullTranscriptRef.current = '';
        triggeredRef.current = false;
        crmCaptureRef.current = '';
      } catch (error) {
        alert('Microphone access denied.');
      }
    } else {
      setIsRecording(false);
      if (recognitionRef.current) {
        recognitionRef.current.onend = null;
        recognitionRef.current.stop();
      }

      const finalText = crmCaptureRef.current.trim();
      if (finalText.length > 5) {
        console.log('ðŸ” Sending CRM content to DeepSeek:', finalText);
        try {
          const updates = await sendToDeepSeek(finalText);
          console.log('ðŸ“¤ Parsed updates:', updates);

          if (updates.length > 0) {
            await sendToAttio(updates);
            console.log('âœ… Successfully sent to Attio');
          } else {
            console.log('â„¹ï¸ No valid updates extracted');
          }
        } catch (error) {
          console.error('âŒ Error processing CRM data:', error);
        }
      } else {
        console.log('â„¹ï¸ No CRM content captured.');
      }
    }
  };

  useEffect(() => {
    if (!('webkitSpeechRecognition' in window) && !('SpeechRecognition' in window)) {
      alert('Speech recognition not supported.');
      return;
    }

    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    recognitionRef.current = new SpeechRecognition();
    recognitionRef.current.continuous = true;
    recognitionRef.current.interimResults = true;
    recognitionRef.current.lang = 'en-US';

    recognitionRef.current.onresult = (event) => {
      let interim = '', final = '';
      for (let i = event.resultIndex; i < event.results.length; i++) {
        const transcript = event.results[i][0].transcript;
        if (event.results[i].isFinal) {
          final += transcript + ' ';
        } else {
          interim += transcript;
        }
      }

      const fullText = currentTextRef.current + final + interim;
      updateLyricDisplay(fullText);

      if (final) {
        currentTextRef.current += final;
        fullTranscriptRef.current += final + ' ';

        const clean = final.toLowerCase().trim();
        if (!triggeredRef.current && clean.includes(triggerPhrase.toLowerCase())) {
          console.log("âœ… Trigger phrase detected");
          triggeredRef.current = true;
          crmCaptureRef.current = '';
        }
        else if (triggeredRef.current) {
          crmCaptureRef.current += final + ' ';
        }
      }
    };

    recognitionRef.current.onerror = (e) => console.error('Speech error:', e.error);
    recognitionRef.current.onend = () => {
      if (isRecording) recognitionRef.current.start();
    };

    return () => recognitionRef.current?.stop();
  }, [isRecording]);

  return (
    <div className="speech-recorder">
      <div className="lyric-display">
        <div className={`previous-lines ${isAnimating ? 'animating' : ''}`}>
          <div className="lyric-line">{previousLines[0]}</div>
          <div className="lyric-line">{previousLines[1]}</div>
        </div>
        <div className="current-lines">
          <div className="lyric-line">{currentLines[0]}</div>
          <div className="lyric-line">{currentLines[1]}</div>
        </div>
        <div className="gradient-overlay" />
      </div>
      <button onClick={toggleRecording} className={`mic-button ${isRecording ? 'recording' : 'idle'}`}>
        {isRecording && <>
          <div className="pulse-ring pulse-ping"></div>
          <div className="pulse-ring pulse-beat"></div>
        </>}
        <div className="mic-icon">{isRecording ? <MicOff size={32} /> : <Mic size={32} />}</div>
      </button>
      <div className="status-text">
        {isRecording ? 'Recording... say "initiate CRM" to start logging' : 'Click to start recording'}
      </div>
    </div>
  );
};

export default SpeechRecorder;
